import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

import Header from './components/Header';
import HeroSection from './components/HeroSection';
import FeatureSection from './components/FeatureSection';
import BenefitsSection from './components/BenefitsSection';
import FeaturesGrid from './components/FeaturesGrid';
import CTASection from './components/CTASection';
import Footer from './components/Footer';

const AppContainer = styled.div`
  position: relative;
  min-height: 100vh;
  overflow-x: hidden;
`;

const ContentWrapper = styled.div`
  position: relative;
  z-index: 1;
`;

const sections = [
  {
    id: 'hero',
    title: 'AI Recognition',
    description: 'Revolutionary cattle breed identification using advanced AI technology',
    phoneContent: 'camera',
  },
  {
    id: 'features',
    title: 'Smart Detection',
    description: 'Real-time breed recognition with 99% accuracy',
    phoneContent: 'results',
  },
  {
    id: 'benefits',
    title: 'Farmer Benefits',
    description: 'Empowering farmers with data-driven livestock management',
    phoneContent: 'dashboard',
  },
  {
    id: 'grid',
    title: 'AI Features',
    description: 'Comprehensive AI-powered livestock management tools',
    phoneContent: 'features',
  },
  {
    id: 'cta',
    title: 'Get Started',
    description: 'Join thousands of farmers using AI for better livestock management',
    phoneContent: 'download',
  }
];

function App() {
  const [activeSection, setActiveSection] = useState(0);
  const [scrollY, setScrollY] = useState(0);

  // Track scroll position
  useEffect(() => {
    const handleScroll = () => setScrollY(window.scrollY);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Section observers
  const sectionRefs = sections.map(() => {
    const { ref, inView } = useInView({
      threshold: 0.5,
      rootMargin: '-20% 0px -20% 0px'
    });
    return { ref, inView };
  });

  // Update active section based on which section is in view
  useEffect(() => {
    const activeSectionIndex = sectionRefs.findIndex(({ inView }) => inView);
    if (activeSectionIndex !== -1) {
      setActiveSection(activeSectionIndex);
    }
  }, [sectionRefs.map(({ inView }) => inView)]);

  return (
    <AppContainer>
      <Header />
      
      <PhoneContainer>
        <PhoneMockup 
          activeSection={activeSection}
          phoneContent={sections[activeSection]?.phoneContent || 'camera'}
          sectionData={sections[activeSection]}
        />
      </PhoneContainer>

      <ContentWrapper>
        <div ref={sectionRefs[0].ref}>
          <HeroSection />
        </div>

        <div ref={sectionRefs[1].ref}>
          <FeatureSection 
            layout="right"
            title="Intelligent Breed Recognition"
            subtitle="Advanced AI Technology"
            description="Our cutting-edge AI system can identify over 50 cattle and buffalo breeds native to India with 99% accuracy. Simply point your camera at the animal and get instant results."
            features={[
              "50+ Indian cattle breeds supported",
              "Real-time recognition in 2 seconds",
              "Works in various lighting conditions",
              "Offline capability for remote areas"
            ]}
          />
        </div>

        <div ref={sectionRefs[2].ref}>
          <BenefitsSection 
            layout="left"
            title="Empowering Farmers"
            subtitle="Data-Driven Decisions"
            description="Make informed decisions about breeding, feeding, and healthcare with AI-powered insights tailored for Indian livestock management."
            benefits={[
              "Optimize breeding programs",
              "Prevent genetic diseases",
              "Track lineage and genetics",
              "Connect with veterinarians"
            ]}
          />
        </div>

        <div ref={sectionRefs[3].ref}>
          <FeaturesGrid />
        </div>

        <div ref={sectionRefs[4].ref}>
          <CTASection />
        </div>
      </ContentWrapper>

      <Footer />
    </AppContainer>
  );
}

export default App;
