import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const FooterContainer = styled.footer`
  background: linear-gradient(135deg, #1e293b 0%, #334155 100%);
  color: white;
  padding: 3rem 0 1rem;
  position: relative;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><defs><pattern id="dots" width="20" height="20" patternUnits="userSpaceOnUse"><circle cx="10" cy="10" r="1" fill="%23ffffff" opacity="0.05"/></pattern></defs><rect width="100" height="100" fill="url(%23dots)"/></svg>');
  }
`;

const ContentWrapper = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 var(--spacing-lg);
  position: relative;
  z-index: 1;
`;

const FooterContent = styled.div`
  display: grid;
  grid-template-columns: 2fr 1fr 1fr 1fr;
  gap: 3rem;
  margin-bottom: 2rem;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 2rem;
    text-align: center;
  }
`;

const CompanyInfo = styled.div`
  h3 {
    font-family: var(--font-display);
    font-size: 1.5rem;
    font-weight: 700;
    color: #2563eb;
    margin-bottom: 1rem;
    display: flex;
    align-items: center;
    gap: 0.5rem;
    
    &::before {
      content: '🐄';
      font-size: 2rem;
    }
    
    @media (max-width: 768px) {
      justify-content: center;
    }
  }
  
  p {
    color: #64748b;
    line-height: 1.6;
    margin-bottom: 1.5rem;
  }
`;

const SocialLinks = styled.div`
  display: flex;
  gap: 1rem;
  
  @media (max-width: 768px) {
    justify-content: center;
  }
`;

const SocialLink = styled.a`
  width: 40px;
  height: 40px;
  background: #2563eb;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  text-decoration: none;
  font-size: 1.2rem;
  transition: all 0.3s ease;
  
  &:hover {
    background: #1d4ed8;
    transform: translateY(-2px);
  }
`;

const FooterSection = styled.div`
  h4 {
    font-family: var(--font-display);
    font-size: 1.1rem;
    font-weight: 600;
    color: #1e293b;
    margin-bottom: 1rem;
  }
  
  ul {
    list-style: none;
    
    li {
      margin-bottom: 0.5rem;
      
      a {
        color: #64748b;
        text-decoration: none;
        transition: color 0.3s ease;
        
        &:hover {
          color: #2563eb;
        }
      }
    }
  }
`;

const Copyright = styled.div`
  border-top: 1px solid #cbd5e1;
  padding-top: 2rem;
  text-align: center;
  color: #64748b;
  font-size: 0.9rem;
  
  @media (max-width: 768px) {
    padding-top: 1rem;
  }
`;

const Footer = () => {
  return (
    <FooterContainer>
      <ContentWrapper>
        <FooterContent>
          <CompanyInfo>
            <h3>AI Cattle Recognition</h3>
            <p>
              Empowering Indian farmers with cutting-edge AI technology for better livestock management. 
              Our mission is to revolutionize agriculture through innovative breed recognition and 
              data-driven insights.
            </p>
            <SocialLinks>
              <SocialLink href="#" aria-label="Facebook">📘</SocialLink>
              <SocialLink href="#" aria-label="Twitter">🐦</SocialLink>
              <SocialLink href="#" aria-label="Instagram">📷</SocialLink>
              <SocialLink href="#" aria-label="LinkedIn">💼</SocialLink>
              <SocialLink href="#" aria-label="YouTube">📺</SocialLink>
            </SocialLinks>
          </CompanyInfo>
          
          <FooterSection>
            <h4>Product</h4>
            <ul>
              <li><a href="#features">Features</a></li>
              <li><a href="#benefits">Benefits</a></li>
              <li><a href="#pricing">Pricing</a></li>
              <li><a href="#demo">Demo</a></li>
              <li><a href="#updates">Updates</a></li>
            </ul>
          </FooterSection>
          
          <FooterSection>
            <h4>Support</h4>
            <ul>
              <li><a href="#help">Help Center</a></li>
              <li><a href="#contact">Contact Us</a></li>
              <li><a href="#training">Training</a></li>
              <li><a href="#community">Community</a></li>
              <li><a href="#feedback">Feedback</a></li>
            </ul>
          </FooterSection>
          
          <FooterSection>
            <h4>Company</h4>
            <ul>
              <li><a href="#about">About Us</a></li>
              <li><a href="#careers">Careers</a></li>
              <li><a href="#news">News</a></li>
              <li><a href="#partners">Partners</a></li>
              <li><a href="#investors">Investors</a></li>
            </ul>
          </FooterSection>
        </FooterContent>
        
        <Copyright>
          <p>
            © 2024 AI Cattle Recognition. All rights reserved. | 
            <a href="#privacy" style={{ color: '#2563eb', marginLeft: '0.5rem' }}>Privacy Policy</a> | 
            <a href="#terms" style={{ color: '#2563eb', marginLeft: '0.5rem' }}>Terms of Service</a>
          </p>
          <p style={{ marginTop: '0.5rem', fontSize: '0.8rem' }}>
            Made with ❤️ for Indian farmers | Hackathon Project 2024
          </p>
        </Copyright>
      </ContentWrapper>
    </FooterContainer>
  );
};

export default Footer;
